    return jsonify({'success': True, 'message': 'Registration successful - pending admin approval'})

@app.route('/api/staff/pending', methods=['GET'])
def staff_pending():
    pending = Staff.query.filter_by(is_approved=False).all()
    return jsonify([{
        'id': s.id,
        'email': s.email,
        'phone': s.phone,
