@app.route('/api/staff/<int:staff_id>/approve', methods=['POST'])
def staff_approve(staff_id):
    staff = Staff.query.get_or_404(staff_id)
    data = request.json
    approved = data.get('approved', True)

    if approved:
        staff.is_approved = True
        db.session.commit()
