const API_BASE = '';
let authToken = localStorage.getItem('staff_token');
let currentOrders = [];

document.addEventListener('DOMContentLoaded', () => {
    if (authToken) {
        checkTrackingLink();
    }

    setupEventListeners();
});

function setupEventListeners() {
    const loginForm = document.getElementById('staff-login-form');
    if (loginForm) {
        loginForm.addEventListener('submit', handleLogin);
    }

    const registerForm = document.getElementById('staff-register-form');
    if (registerForm) {
        registerForm.addEventListener('submit', handleRegister);
    }

    const trackingForm = document.getElementById('tracking-link-form');
    if (trackingForm) {
        trackingForm.addEventListener('submit', handleTrackingLink);
    }
}

async function handleLogin(e) {
    e.preventDefault();

    const email = document.getElementById('staff-email').value;
    const password = document.getElementById('staff-password').value;

    try {
        const response = await fetch(`${API_BASE}/api/staff/login`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ email, password })
        });

        const data = await response.json();

        if (data.success) {
            authToken = data.token;
            localStorage.setItem('staff_token', authToken);

            if (data.needs_tracking_link) {
                showPage('tracking-link-page');
            } else {
                showPage('dashboard-page');
                loadDashboard();
            }
        } else {
            showError(data.message);
        }
    } catch (error) {
        showError('Error: ' + error.message);
    }
}

async function handleRegister(e) {
    e.preventDefault();

    const name = document.getElementById('reg-name').value;
    const email = document.getElementById('reg-email').value;
    const phone = document.getElementById('reg-phone').value;
    const password = document.getElementById('reg-password').value;

    try {
        const response = await fetch(`${API_BASE}/api/staff/register`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ full_name: name, email, phone, password })
        });

        const data = await response.json();

        if (data.success) {
            alert(data.message);
            showLogin();
        } else {
            showError(data.message);
        }
    } catch (error) {
        showError('Error: ' + error.message);
    }
}

async function handleTrackingLink(e) {
    e.preventDefault();

    const link = document.getElementById('tracking-link-input').value;

    try {
        const response = await fetch(`${API_BASE}/api/staff/tracking-link`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${authToken}`
            },
            body: JSON.stringify({ link })
        });

        const data = await response.json();

        if (data.success) {
            showPage('dashboard-page');
            loadDashboard();
        } else {
            alert('Error: ' + data.message);
        }
    } catch (error) {
        alert('Error: ' + error.message);
    }
}

async function checkTrackingLink() {
    showPage('dashboard-page');
    loadDashboard();
}

async function loadDashboard() {
    await loadOrders();
    await loadStats();
    setInterval(loadOrders, 10000);
}

async function loadOrders() {
    try {
        const response = await fetch(`${API_BASE}/api/orders`, {
            headers: { 'Authorization': `Bearer ${authToken}` }
        });

        currentOrders = await response.json();
        renderOrders();
    } catch (error) {
        console.error('Error loading orders:', error);
    }
}

function renderOrders() {
    const availableDiv = document.getElementById('available-orders');
    const myOrdersDiv = document.getElementById('my-orders');

    const available = currentOrders.filter(o => !o.staff_id && !o.is_archived);
    const myOrders = currentOrders.filter(o => o.staff_id && !o.is_archived);

    availableDiv.innerHTML = available.map(order => `
        <div class="order-card">
            <h4>Order #${order.order_id}</h4>
            <p>Customer: ${order.customer_name}</p>
            <p>Total: KES ${order.total_amount}</p>
            <p>Address: ${order.delivery_address}</p>
            <button onclick="claimOrder(${order.id})">Accept Order</button>
        </div>
    `).join('') || '<p>No available orders</p>';

    myOrdersDiv.innerHTML = myOrders.map(order => `
        <div class="order-card">
            <h4>Order #${order.order_id}</h4>
            <p>Customer: ${order.customer_name}</p>
            <p>Phone: ${order.customer_phone}</p>
            <p>Total: KES ${order.total_amount}</p>
            <p>Payment: ${order.payment_status}</p>
            <button onclick="viewOrderDetail(${order.id})">View Details</button>
            <button onclick="unclaimOrder(${order.id})">Unclaim</button>
        </div>
    `).join('') || '<p>No active orders</p>';
}

async function claimOrder(orderId) {
    try {
        const response = await fetch(`${API_BASE}/api/orders/${orderId}/claim`, {
            method: 'POST',
            headers: { 'Authorization': `Bearer ${authToken}` }
        });

        const data = await response.json();

        if (data.success) {
            loadOrders();
        } else {
            alert(data.message);
        }
    } catch (error) {
        alert('Error: ' + error.message);
    }
}

async function unclaimOrder(orderId) {
    try {
        const response = await fetch(`${API_BASE}/api/orders/${orderId}/unclaim`, {
            method: 'POST',
            headers: { 'Authorization': `Bearer ${authToken}` }
        });

        const data = await response.json();

        if (data.success) {
            loadOrders();
        }
    } catch (error) {
        alert('Error: ' + error.message);
    }
}

function viewOrderDetail(orderId) {
    const order = currentOrders.find(o => o.id === orderId);
    if (!order) return;

    const modal = document.getElementById('order-detail-modal');
    const content = document.getElementById('order-detail-content');

    content.innerHTML = `
        <h3>Order #${order.order_id}</h3>
        <p><strong>Customer:</strong> ${order.customer_name}</p>
        <p><strong>Phone:</strong> ${order.customer_phone}</p>
        <p><strong>Address:</strong> ${order.delivery_address}</p>
        <p><strong>Total:</strong> KES ${order.total_amount}</p>
        <p><strong>Payment Status:</strong> ${order.payment_status}</p>
        <h4>Actions:</h4>
        <button onclick="callCustomer('${order.customer_phone}')">üìû Call Customer</button>
        ${order.payment_status !== 'Payment Complete' ? 
            `<button onclick="requestPayment(${order.id})">Request Payment</button>
             <button onclick="markPaid(${order.id})">Mark as Paid (Cash)</button>` : ''}
        <button onclick="deliverOrder(${order.id})">‚úì Confirm Product Delivered</button>
    `;

    modal.style.display = 'block';
}

function callCustomer(phone) {
    const choice = confirm('Call via WhatsApp? (Cancel for normal call)');
    if (choice) {
        window.open(`https://wa.me/${phone}`, '_blank');
    } else {
        window.location.href = `tel:+${phone}`;
    }
}

async function requestPayment(orderId) {
    try {
        const response = await fetch(`${API_BASE}/api/orders/${orderId}/payment`, {
            method: 'POST',
            headers: { 'Authorization': `Bearer ${authToken}` }
        });

        const data = await response.json();
        alert(data.message);

        if (data.success) {
            closeModal();
            loadOrders();
        }
    } catch (error) {
        alert('Error: ' + error.message);
    }
}

async function markPaid(orderId) {
    try {
        const response = await fetch(`${API_BASE}/api/orders/${orderId}/mark-paid`, {
            method: 'POST',
            headers: { 'Authorization': `Bearer ${authToken}` }
        });

        const data = await response.json();

        if (data.success) {
            alert('Payment marked as complete');
            closeModal();
            loadOrders();
        }
    } catch (error) {
        alert('Error: ' + error.message);
    }
}

async function deliverOrder(orderId) {
    if (!confirm('Confirm that the product has been delivered?')) return;

    try {
        const response = await fetch(`${API_BASE}/api/orders/${orderId}/deliver`, {
            method: 'POST',
            headers: { 'Authorization': `Bearer ${authToken}` }
        });

        const data = await response.json();

        if (data.success) {
            alert('Order marked as delivered');
            closeModal();
            loadOrders();
        }
    } catch (error) {
        alert('Error: ' + error.message);
    }
}

async function loadStats() {
    const stats = {
        today_deliveries: currentOrders.filter(o => o.staff_id && o.status === 'Delivered').length,
        completed: currentOrders.filter(o => o.is_archived).length,
        pending: currentOrders.filter(o => !o.is_archived).length,
        cash: currentOrders.filter(o => o.payment_method === 'Cash').length,
        mpesa: currentOrders.filter(o => o.payment_method === 'Pay Now').length
    };

    document.getElementById('staff-stats').innerHTML = `
        <div class="stat-card"><div class="stat-label">Today's Deliveries</div><div class="stat-value">${stats.today_deliveries}</div></div>
        <div class="stat-card"><div class="stat-label">Completed Orders</div><div class="stat-value">${stats.completed}</div></div>
        <div class="stat-card"><div class="stat-label">Pending Orders</div><div class="stat-value">${stats.pending}</div></div>
        <div class="stat-card"><div class="stat-label">Cash Payments</div><div class="stat-value">${stats.cash}</div></div>
        <div class="stat-card"><div class="stat-label">M-Pesa Payments</div><div class="stat-value">${stats.mpesa}</div></div>
    `;
}

function showPage(pageId) {
    document.querySelectorAll('.page').forEach(p => p.style.display = 'none');
    document.getElementById(pageId).style.display = 'block';
}

function showLogin() {
    document.getElementById('login-view').style.display = 'block';
    document.getElementById('register-view').style.display = 'none';
}

function showRegister() {
    document.getElementById('login-view').style.display = 'none';
    document.getElementById('register-view').style.display = 'block';
}

function showError(message) {
    const errorDiv = document.getElementById('error-message');
    errorDiv.textContent = message;
    errorDiv.style.display = 'block';
}

function closeModal() {
    document.getElementById('order-detail-modal').style.display = 'none';
}

function logout() {
    localStorage.removeItem('staff_token');
    location.reload();
}

